const nav=document.querySelector(".navbar"),hero=document.querySelector(".hero"),about=document.querySelector(".article__about"),menus=document.querySelector(".article__menus"),christmas=document.querySelector(".article__christmas"),map=document.querySelector(".article__map"),times=document.querySelector(".article__times--home"),elementsToFade=[about,menus,christmas,map,times];let scrollPoints=[];const getScrollPoints=e=>{let o=[];e.forEach(e=>{o.push(e.offsetHeight)});for(let e=0;e<o.length;e++)o.indexOf(o[e])>0?scrollPoints.push(o[e]+=o[e-1]):scrollPoints.push(o[e])},fadeInOnScroll=(e,o)=>{window.scrollY+e.offsetHeight/2>=o&&e.classList.add("show")};window.onscroll=(()=>{getScrollPoints(elementsToFade),fadeInOnScroll(about,scrollPoints[0]),fadeInOnScroll(menus,scrollPoints[1]),fadeInOnScroll(christmas,scrollPoints[2]),fadeInOnScroll(map,scrollPoints[3]),fadeInOnScroll(times,scrollPoints[4])});