const nav=document.querySelector(".navbar"),hero=document.querySelector(".hero"),about=document.querySelector(".article__about"),menus=document.querySelector(".article__menus"),christmas=document.querySelector(".article__christmas"),map=document.querySelector(".article__map"),times=document.querySelector(".article__times--home"),gallery=document.querySelector(".gallery"),elementsToFade=[about,menus,map,times,gallery];let scrollPoints=[];const getScrollPoints=e=>{let l=[];e.forEach(e=>{l.push(e.offsetHeight)});for(let o=0;o<l.length;o++)l.indexOf(l[o])>0?scrollPoints.push(l[o]+=l[o-1]):scrollPoints.push(l[o])},fadeInOnScroll=(e,l)=>{window.scrollY+e.offsetHeight/2>=l&&e.classList.add("show")};window.onscroll=()=>{getScrollPoints(elementsToFade),fadeInOnScroll(about,scrollPoints[0]),fadeInOnScroll(menus,scrollPoints[1]),fadeInOnScroll(christmas,scrollPoints[2]),fadeInOnScroll(map,scrollPoints[2]),fadeInOnScroll(times,scrollPoints[3]),fadeInOnScroll(gallery,scrollPoints[4])};